init:
$('body').addClass('textured');
$('#subheader').text(OF.page.title);
var form, pwField;
pwField = $('#password');
form = $('#parental_controls_form');
$('#submit').touch(function() {
  return form.submit();
});
form.submit(function() {
  OF.api["delete"]("/xp/devices/" + OF.device.identifier + "/parental_control", {
    params: {
      password: pwField.val()
    },
    success: function() {
      OF.device.parentalControls = false;
      if (OF.action.isSupported('setParentalControlsEnabled')) {
        OF.action('setParentalControlsEnabled', {
          parentalControlsEnabled: "false"
        });
      }
      return OF.goBack();
    },
    failure: function() {
      return OF.alert('Wrong password', 'Please try again');
    }
  });
  return false;
});
