init:
$('body').addClass('textured');
$('#subheader').text(OF.page.title);
$('#submit').touch(function() {
  var button = $(this);
  if (!button.isLoading()) {
    button.isLoading(true);
    OF.api.request('/xp/devices/'+ OF.device.identifier + "/users/" + OF.user.id, {
      method: 'DELETE',
      loader: '#submit',
      success: function() {
        alert("Your account has been successfully removed from this device.");
        OF.sendAction('logout');
      },
      complete: function() {
        button.isLoading(false);
      }
    });
  }
});
