init:
$('body').addClass('textured');
$('#header .loading').hide();
$('.button').pretouch();

if (OF.page.params.mode == 'switch') {
  OF.introSwitch = true;
}

if (OF.introSwitch) {
  $('.switch_flow').unhide(0);
  $('.intro_flow').hide();
}
var userTmpl = tmpl('user_tmpl');
var html = '';
$.each(OF.users, function() {
  html += userTmpl(this.user);
});
$('#multiple_user_accounts').hide().html(html).fadeIn();
