init:
$('body').addClass('textured');$('.button').pretouch();if(!OF.menu){OF.menu=function(buttonName){if(buttonName=='home')OF.menu.home();else if(buttonName=='settings')OF.menu.settings();else if(buttonName=='exit')OF.menu.exit();else{OF.log('Unknown menu button hit: '+buttonName);}};OF.menu.home=function(){if(OF.navigationStack[0].url.match(/dashboard\/user/)){OF.goBack({root:true});}else{OF.navigateToUrl("dashboard/user",{complete:function(){OF.pages.unshift(OF.pages.pop());OF.action('back',{root:true},OF.contentLoaded);}});}};OF.menu.settings=function(){OF.sendAction('openSettings');};OF.menu.exit=function(){OF.sendAction('dismiss');};}
if(!OF.friendsList){OF.friendsList={init:function(){jQuery.lazyLoad({threshold:0.5,nextPageLoaded:function(page,data){if(page==1){OF.friendsList.renderFriends(data);}else{OF.friendsList.addFriends(data);}},nextPageShouldLoad:function(page,callback){OF.api('/xp/friends',{params:{user_id:OF.page.user.id,page:page,page_size:50},success:function(data){callback(data.users,data.users.length<50)},failure:function(){callback([]);}});},isEmpty:function(){OF.friendsList.renderFriends([]);},isFinished:function(){jQuery("#loading").hide();}});},renderFriendItems:function(friends){var html=jQuery.map(friends,function(friend){return tmpl('user_tmpl',friend);});return html;},renderFriends:function(friends){var html=OF.friendsList.renderFriendItems(friends);if(html.length>0){jQuery('#friends_list > ul').html(html.join('')).unhide();jQuery("#friends_list .no_data").hide();}else{jQuery('#friends_list > ul').hide();jQuery("#friends_list .no_data").unhide();}},addFriends:function(friends){var html=OF.friendsList.renderFriendItems(friends);jQuery('#friends_list > ul').append(html.join(''));}};}
$('#home_button').touch(OF.menu.home);var prompt='Search by Feint Name';var field=$('#username_field');field.focus(function(){field.removeClass('prompt');if(field.val()==prompt){field.val('');}});field.blur(function(){if($.trim(field.val()).length==0){field.val(prompt).addClass('prompt');}});field.blur();$('#submit').touch(function(){$('li.user').fadeOut();var username=$('#username_field').val();if(username.length>0&&username!=prompt){$('#friends_list').unhide();OF.api.request('/xp/friends/search',{params:{name:username},loader:'#friends_list .loading',success:function(data){if(data.users.length>0){var userTmpl=tmpl('user_tmpl');var html=[];$.each(data.users,function(){html.push(userTmpl(this));});$('li.user').remove();$('#friends_list').append(html.join('')).removeClass('hidden').unhide();}else{OF.alert("No users","Unable to find any users by that name.");}},});}else{OF.alert("Invalid name","Please enter a user name to search for friends.");}});$('form').submit(function(){$('#submit').touch();return false;});
