init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
$('#submit').touch(function(){var email=$.trim($('#user_email').val());if(email.length==0||!email.match(/.+@.+/)){OF.alert('Invalid Email','Enter your email to retrieve your Feint account password');return;}
OF.api.request('/xp/users/reset_password',{method:'PUT',params:{email:email},loader:'#submit',success:function(){OF.goBack();OF.alert('Success','Your password has been reset and emailed to you.');},failure:function(){OF.alert('Incorrect Email','No user was found with that email address, double check that it is correct and try again');}});});
