init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
if(OF.users.length>1&&OF.page.params.user_id){$.each(OF.users,function(){if(this.user.id==OF.page.params.user_id){OF.user=this.user;}});}
$('.game_name').html(OF.game.name);$('.user_name').html(OF.user.name);$('.feint_score').html(OF.user.gamer_score);if(OF.user.profile_picture_url){$('.avatar').css('background-image','url('+OF.user.profile_picture_url+')');}
OF.page.boostrapCallback=function(status,response){$('form').data('isLoading',false);OF.loader.hide();if(status.match(/^2/)){OF.log('Successfully bootstrapped with user name: "'+OF.user.name+'" id: '+OF.user.id);OF.sendAction('dismiss');}else if(status.match(/404/)){OF.alert('Account not found','There is no Feint account with that username/email.');}else if(status.match(/401/)){OF.alert('Incorrect Password','The password entered is incorrect.');}else{var message=(response.exception&&response.exception.message)?$.htmlDecode(response.exception.message):'Check your username and password and try again';OF.alert('Authentication failed',message);}};$('#submit').touch(function(){if(!$('form').data('isLoading')){$('form').data('isLoading',true);OF.loader.show();OF.sendAction('loginUser',{user_id:OF.user.id,password:$('#user_password').val(),callback:'OF.page.boostrapCallback'});}});$('form').submit(function(){OF.touch('#submit');return false;});
