init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
$('.game_name').html(OF.game.name);if(OF.spotlight){$('.intro_flow').hide();$('.intro_flow_spotlight').unhide();}
OF.page.boostrapCallback=function(status,response){$('form').isLoading(false);$("#submit").text("Start Playing");OF.loader.hide();if(status.match(/^2/)){OF.log('Successfully bootstrapped');OF.sendAction('dismiss');}else if(status.match(/404/)){OF.alert('Account not found','There is no Feint account with that username/email.');}else if(status.match(/401/)){OF.alert('Incorrect Password','The password entered is incorrect.');}else{var message=response.exception.message?$.htmlDecode(response.exception.message):'Check your email address and password and try again';OF.alert('Authentication failed',message);}}
$('#submit').touch(function(){if(!$('form').isLoading()){var email=$.trim($('#user_email').val());var password=$('#user_password').val();$('input').blur();if(email.length&&password.length){$('form').data('isLoading',true);OF.loader.show();$(this).html('Loading&hellip;');OF.sendAction('loginUser',{email:$.trim($('#user_email').val()),password:$('#user_password').val(),callback:'OF.page.boostrapCallback'});}else{$('form').isLoading(true);setTimeout(function(){$('form').isLoading(false);},500);OF.alert('Missing Data','Enter your username or email and your password to login.')}}});$('form').submit(function(){$('#submit').touch();return false;});
