init:
$('body').addClass('textured');$('.button').pretouch();if(!OF.menu){OF.menu=function(buttonName){if(buttonName=='home')OF.menu.home();else if(buttonName=='settings')OF.menu.settings();else if(buttonName=='exit')OF.menu.exit();else{OF.log('Unknown menu button hit: '+buttonName);}};OF.menu.home=function(){if(OF.navigationStack[0].url.match(/dashboard\/user/)){OF.goBack({root:true});}else{OF.navigateToUrl("dashboard/user",{complete:function(){OF.pages.unshift(OF.pages.pop());OF.action('back',{root:true},OF.contentLoaded);}});}};OF.menu.settings=function(){OF.sendAction('openSettings');};OF.menu.exit=function(){OF.sendAction('dismiss');};}
if(!OF.friendsList){OF.friendsList={init:function(){jQuery.lazyLoad({threshold:0.5,nextPageLoaded:function(page,data){if(page==1){OF.friendsList.renderFriends(data);}else{OF.friendsList.addFriends(data);}},nextPageShouldLoad:function(page,callback){OF.api('/xp/friends',{params:{user_id:OF.page.user.id,page:page,page_size:50},success:function(data){callback(data.users,data.users.length<50)},failure:function(){callback([]);}});},isEmpty:function(){OF.friendsList.renderFriends([]);},isFinished:function(){jQuery("#loading").hide();}});},renderFriendItems:function(friends){var html=jQuery.map(friends,function(friend){return tmpl('user_tmpl',friend);});return html;},renderFriends:function(friends){var html=OF.friendsList.renderFriendItems(friends);if(html.length>0){jQuery('#friends_list > ul').html(html.join('')).unhide();jQuery("#friends_list .no_data").hide();}else{jQuery('#friends_list > ul').hide();jQuery("#friends_list .no_data").unhide();}},addFriends:function(friends){var html=OF.friendsList.renderFriendItems(friends);jQuery('#friends_list > ul').append(html.join(''));}};}
$('#home_button').touch(OF.menu.home);var game=OF.page.params.game;OF.page.game=game;$('li#game_info .icon').css('background-image','url('+game.android_icon_url+')');$('li#game_info .title').text(game.name);$('li#game_info .subtitle').text(game.developer_name);$('.thumbnail').touch(function(){if($(this).hasClass('youtube')){OF.sendAction('openYoutubePlayer',{video_id:OF.page.game.description.youtube.video_id});}else{var mainImg=$('#main')
var tappedImg=$(this).children('img');mainImg.css('background-image','url('+tappedImg[0].src+')');mainImg[0].className=tappedImg.data('isLandscape')?'landscape':'';}});OF.api.request('/xp/games/'+game.id+'/descriptions/android',{method:'GET',success:function(data){OF.page.game.description=data.description;$('#buy .price').text('$'+OF.page.game.description.price);$('.game_description').html(OF.page.game.description.description.replace(/\n/g,'<br>'));if(OF.page.game.description.screenshots.length){var lastThumbnailIdx=-1;$('.thumbnail').hide();$.each(OF.page.game.description.screenshots.slice(0,4),function(i){lastThumbnailIdx++;$('.thumbnail:eq('+i+')').show();$('.thumbnail:eq('+i+') img').data('isLandscape',this.is_landscape)
[0].src=this.url;});$('#main').css('background-image','url('+OF.page.game.description.screenshots[0].url+')')
[0].className=(OF.page.game.description.screenshots[0].is_landscape?'landscape':'');if(OF.page.game.description.screenshots.length&&OF.page.game.description.youtube.video_id&&OF.page.game.description.youtube.video_id.length){$('.thumbnail:eq('+lastThumbnailIdx+')').addClass('youtube').append("<span class='movie_play_overlay'></span>")}}
OF.page.onIsInstalled=function(installed){if(installed){$('#buy').addClass('disabled').text('Installed').unhide();}else{$('#buy').unhide().touch(function(){OF.sendAction('openMarket',{package_name:OF.page.game.description.package_identifier});});}}
if(OF.isDevice){OF.sendAction('isApplicationInstalled',{package_name:OF.page.game.description.package_identifier,callback:'OF.page.onIsInstalled'});}else{OF.page.onIsInstalled(false);}},on404:function(data){OF.goBack();OF.alert('No Game Info','The developer of this game has not yet setup all the information for this screen.');},failure:$.noop});
