init:
$('body').addClass('textured');$('#header .loading').hide();$('.button').pretouch();if(OF.page.params.mode=='switch'){OF.introSwitch=true;}
if(OF.introSwitch){$('.switch_flow').unhide(0);$('.intro_flow').hide();}
$('.game_name').html(OF.game.name);if(OF.actionIsSupported('getEmail')){OF.sendAction('getEmail',{callback:function(email){$('#user_email').val(email);}});}
OF.page.bootstrapCallback=function(status,response){$('form').isLoading(false);$('#submit').html('Start Playing!');if(typeof response==='string')response=$.parseJSON(response);OF.loader.hide();if(status.match(/^2/)){OF.GA.push("_trackPageview","/webui/user_created");OF.log('Successfully bootstrapped with new account');OF.sendAction('dismiss');}else{var message=response.exception.message?$.htmlDecode(response.exception.message):'We had an error processing your information.  Ensure it all looks right and try again.';OF.alert('Error',message);}};$('#submit').touch(function(){if(!$('form').isLoading()){$('form').isLoading(true);$('#submit').html('Loading&hellip;');OF.loader.show();OF.sendAction('createUser',{name:OF.page.params.userName,email:$.trim($('#user_email').val()),password:$('#user_password').val(),password_confirmation:$('#user_password_confirmation').val(),callback:'OF.page.bootstrapCallback'});}});$('form').submit(function(){OF.touch('#submit');return false;})
